$(document).ready(function(){const a=document.getElementById("css-skills-chart").getContext("2d"),b=document.getElementById("html-skills-chart").getContext("2d"),c=document.getElementById("js-skills-chart").getContext("2d"),d=document.getElementById("angular-skills-chart").getContext("2d"),e=document.getElementById("gulp-npm-skills-chart").getContext("2d"),f=document.getElementById("git-skills-chart").getContext("2d"),g=document.getElementById("tennis-skills-chart").getContext("2d"),h=document.getElementById("travelling-skills-chart").getContext("2d"),i=document.getElementById("cities-skills-chart").getContext("2d"),j=document.getElementById("motorization-skills-chart").getContext("2d");jobSkillsChartHandler(a,80,"CSS & Sass"),jobSkillsChartHandler(c,75,"JavaScript & jQuery"),jobSkillsChartHandler(b,60,"HTML"),jobSkillsChartHandler(d,65,"Angular 2+"),jobSkillsChartHandler(e,70,"Gulp & npm"),jobSkillsChartHandler(f,85,"Git (TortoiseGit)"),privateSkillsChartHandler(g,85,"Tennis"),privateSkillsChartHandler(h,70,"Travelling"),privateSkillsChartHandler(i,60,"Cities of the world"),privateSkillsChartHandler(j,70,"Motorization")});function jobSkillsChartHandler(a,b,c){new Chart(a,{type:"doughnut",data:{labels:[c+" - "+b+"%"],datasets:[{backgroundColor:["rgba(255, 121, 26, 1)","rgba(255, 255, 255, 0)"],hoverBackgroundColor:["rgba(255, 121, 26, 1)"],hoverBorderColor:["rgba(255, 121, 26, 1)","#FFF"],data:[b,100-b]}]},options:{legend:{labels:{fontColor:"#FFF",fontSize:20},onClick:a=>a.stopPropagation()},cutoutPercentage:70,tooltips:{filter:function(a,b){var c=b.labels[a.index];if(c)return a}}}})}function privateSkillsChartHandler(a,b,c){new Chart(a,{type:"doughnut",data:{labels:[c+" - "+b+"%"],datasets:[{backgroundColor:["#fbd786","rgba(255, 255, 255, 0)"],hoverBackgroundColor:["#fbd786"],hoverBorderColor:["#fbd786","#FFF"],data:[b,100-b]}]},options:{legend:{labels:{fontColor:"#FFF",fontSize:20},onClick:a=>a.stopPropagation()},cutoutPercentage:70,tooltips:{filter:function(a,b){var c=b.labels[a.index];if(c)return a}}}})}